Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing

#Custom Icon
$Base64Image = ""
$IconBytes = [Convert]::FromBase64String($Base64Image)
$ims = New-Object IO.MemoryStream($IconBytes, 0, $IconBytes.Length)
$ims.Write($IconBytes, 0, $IconBytes.Length)

# Create a form
$form = New-Object System.Windows.Forms.Form
$form.Text = "Chuck Norris Jokes"
$form.Size = New-Object System.Drawing.Size(305, 300)
$form.StartPosition = "CenterScreen"
$form.FormBorderStyle = "FixedDialog"
$form.Icon = [System.Drawing.Icon]::FromHandle((New-Object System.Drawing.Bitmap -Argument $ims).GetHIcon())
$form.TopMost = $true
$form.MaximizeBox = $false

$button1 = New-Object System.Windows.Forms.Button
$button1.Size = New-Object System.Drawing.Size(150, 150)
$button1.Location = New-Object System.Drawing.Point(70, 25)
$button1.Image = $form.Icon
$button1.add_Click({
        Start-Job -ScriptBlock {
            $ChuckJoke = (Invoke-RestMethod -Uri https://api.chucknorris.io/jokes/random).value
            $Base64Image = ""
            If ($Base64Image) {
                $ImageFile = "$env:TEMP\ToastLogo.png"
                [byte[]]$Bytes = [convert]::FromBase64String($Base64Image)
                [System.IO.File]::WriteAllBytes($ImageFile, $Bytes)
            }
            $headlineText = 'Random Chuck Norris Joke'
            $bodyText = $ChuckJoke
  
            $ToastImageAndText02 = [Windows.UI.Notifications.ToastTemplateType, Windows.UI.Notifications, ContentType = WindowsRuntime]::ToastImageAndText02
            $TemplateContent = [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]::GetTemplateContent($ToastImageAndText02)
            $TemplateContent.SelectSingleNode('//image[@id="1"]').SetAttribute('src', $ImageFile)
            $TemplateContent.SelectSingleNode('//text[@id="1"]').InnerText = $headlineText
            $TemplateContent.SelectSingleNode('//text[@id="2"]').InnerText = $bodyText
            $AppId = '{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\WindowsPowerShell\v1.0\powershell.exe'
            [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($AppId).Show($TemplateContent)
  
            Add-Type -AssemblyName System.speech
  ([System.Speech.Synthesis.SpeechSynthesizer]::New()).Speak($ChuckJoke)
        }
    })
$form.Controls.Add($button1)

$label1 = New-Object System.Windows.Forms.Label
$label1.Text = "api.chucknorris.io"
$label1.AutoSize = $true
$label1.Location = New-Object System.Drawing.Point(96, 180)
$label1.Add_Click({
        Start-Process "https://api.chucknorris.io/"
    })
$label1.Font = New-Object System.Drawing.Font("Microsoft Sans Serif", 8.25, [System.Drawing.FontStyle]::Underline)
$label1.Cursor = [System.Windows.Forms.Cursors]::Hand

$form.Controls.Add($label1)

$button2 = New-Object System.Windows.Forms.Button
$button2.Size = New-Object System.Drawing.Size(125, 30)
$button2.Text = "Close"
$button2.Location = New-Object System.Drawing.Point(83, 215)
$button2.add_Click({
        $form.Dispose()
    })
$form.Controls.Add($button2)

function Chuckjoke {
    $ChuckJoke = (Invoke-RestMethod -Uri https://api.chucknorris.io/jokes/random).value
    If ($Base64Image) {
        $ImageFile = "$env:TEMP\ToastLogo.png"
        [byte[]]$Bytes = [convert]::FromBase64String($Base64Image)
        [System.IO.File]::WriteAllBytes($ImageFile, $Bytes)
    }
    $headlineText = 'Random Chuck Norris Joke'
    $bodyText = $ChuckJoke
  
    $ToastImageAndText02 = [Windows.UI.Notifications.ToastTemplateType, Windows.UI.Notifications, ContentType = WindowsRuntime]::ToastImageAndText02
    $TemplateContent = [Windows.UI.Notifications.ToastNotificationManager, Windows.UI.Notifications, ContentType = WindowsRuntime]::GetTemplateContent($ToastImageAndText02)
    $TemplateContent.SelectSingleNode('//image[@id="1"]').SetAttribute('src', $ImageFile)
    $TemplateContent.SelectSingleNode('//text[@id="1"]').InnerText = $headlineText
    $TemplateContent.SelectSingleNode('//text[@id="2"]').InnerText = $bodyText
    $AppId = '{1AC14E77-02E7-4E5D-B744-2EB1AE5198B7}\WindowsPowerShell\v1.0\powershell.exe'
    [Windows.UI.Notifications.ToastNotificationManager]::CreateToastNotifier($AppId).Show($TemplateContent)
  
    Add-Type -AssemblyName System.speech
  ([System.Speech.Synthesis.SpeechSynthesizer]::New()).Speak($ChuckJoke)
}

# Make PowerShell Disappear
$windowcode = '[DllImport("user32.dll")] public static extern bool ShowWindowAsync(IntPtr hWnd, int nCmdShow);'
$asyncwindow = Add-Type -MemberDefinition $windowcode -Name Win32ShowWindowAsync -Namespace Win32Functions -PassThru
$null = $asyncwindow::ShowWindowAsync((Get-Process -PID $pid).MainWindowHandle, 0)

# Show the form
$form.ShowDialog() | Out-Null